fn elevator(parens: &str) -> i32 {
    parens.chars()
          .fold(0, |floor, x| floor + resolve_char(x))
}

fn resolve_char(char: char) -> i32 {
    match char {
      '(' => 1,
      ')' => -1,
      _ => panic!()
    }
}

#[test]
fn it_resolves_char_correctly() {
    assert_eq!(resolve_char('('), 1);
    assert_eq!(resolve_char(')'), -1);
}

#[test]
fn it_gets_to_expected_floor() {
    assert_eq!(elevator("(())"), 0);
    assert_eq!(elevator("()()"), 0);
    assert_eq!(elevator("((("), 3);
    assert_eq!(elevator("(()(()("), 3);
    assert_eq!(elevator("))((((("), 3);
    assert_eq!(elevator("())"), -1);
    assert_eq!(elevator("))("), -1);
    assert_eq!(elevator(")))"), -3);
    assert_eq!(elevator(")())())"), -3);
}

#[test]
fn it_finds_first_to_basement() {
    assert_eq!(first_to_basement(")"), 1);
    assert_eq!(first_to_basement("()())"), 5);
}
